// 引入依赖
var express = require('express')
var utility = require('utility')

// 建立express实例
var ap = express()

app.get('/', function(req, res) {
    // 从req.query中取出我们的q参数
    // 如果是post传来的body数据,则是在req.body里面,不过express默认不处理body中的信息,需要引入
    var q = req.query.q

    // 调用utility.md5方法,得到md5之后的值
    // 之所以使用utility这个库来生成md5的值,其实只是习惯问题,每个人都有自己习惯的技术堆栈
    var md5Value = utility.md5(q)
})